contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
    entity_encoding: 'raw',
    branding: false,
    width: '100%',
    theme: 'silver',
    skin: 'Ghostwriter',
    selector: 'textarea:not(.empty-form textarea, .empty-form)',
    content_css: '/static/css/wysiwyg_styles.css',
    menubar: 'file edit insert view format tools',
    visualchars_default_state: false,
    menu: {
        file: { title: 'File', items: 'newdocument restoredraft' },
        edit: { title: 'Edit', items: 'undo redo | cut copy paste | selectall | searchreplace' },
        view: { title: 'View', items: 'code | visualchars visualblocks | preview' },
        insert: { title: 'Insert', items: 'evidenceUpload codesample link' },
        format: { title: 'Format', items: 'bold italic underline strikethrough superscript subscript codeformat | formats fontformats fontsizes align | forecolor | removeformat' },
        tools: { title: 'Tools', items: 'code wordcount' },
      },
    toolbar_mode: 'floating',
    plugins: 'visualchars visualblocks save preview lists image hr autosave advlist code wordcount codesample searchreplace paste link',
    toolbar: 'subscript superscript bold italic underline link blockquote | bullist numlist | codesample codeInline | evidenceUpload | removeformat save',
    contextmenu: 'bold italic link removeformat',
    paste_as_text: true,
    paste_data_images: false,
    browser_spellcheck: true,
    content_style: `
        .left { text-align: left; }
        img.left { float: left; }
        table.left { float: left; }
        .right { text-align: right; }
        img.right { float: right; }
        table.right { float: right; }
        .center { text-align: center; }
        img.center { display: block; margin: 0 auto; }
        table.center { display: block; margin: 0 auto; }
        .full { text-align: justify; }
        img.full { display: block; margin: 0 auto; }
        table.full { display: block; margin: 0 auto; }
        .bold { font-weight: bold; }
        .italic { font-style: italic; }
        .underline { text-decoration: underline; }
        .tablerow1 { background-color: #D3D3D3; }
        blockquote { border-left: 5px solid #ccc; padding: 0.5em; margin: 0.5em; }
    `,
    formats: {
        alignleft: {
            selector: 'p,h1,h2,h3,h4,h5,h6,td,th,div,ul,ol,li,table,img',
            classes: 'left'
        },
        aligncenter: {
            selector: 'p,h1,h2,h3,h4,h5,h6,td,th,div,ul,ol,li,table,img',
            classes: 'center'
        },
        alignright: {
            selector: 'p,h1,h2,h3,h4,h5,h6,td,th,div,ul,ol,li,table,img',
            classes: 'right'
        },
        alignjustify: {
            selector: 'p,h1,h2,h3,h4,h5,h6,td,th,div,ul,ol,li,table,img',
            classes: 'justify'
        },
        bold: {
            inline: 'span',
            classes: 'bold'
        },
        italic: {
            inline: 'span',
            classes: 'italic'
        },
        underline: {
            inline: 'span',
            classes: 'underline',
            exact: true
        },
        strikethrough: {
            inline: 'del'
        },
        subscript: {
            inline: 'sub'
        },
        superscript: {
            inline: 'sup'
        },
        highlight: {
            inline : 'span',
            classes : 'highlight',
            styles : {
                backgroundColor : 'yellow'
            }
        },
        blockquote: {
            block: 'blockquote',
            classes: 'blockquote'
        }
    },
    style_formats: [
        { title: 'Headings', items: [
          { title: 'Heading 1', format: 'h1' },
          { title: 'Heading 2', format: 'h2' },
          { title: 'Heading 3', format: 'h3' },
          { title: 'Heading 4', format: 'h4' },
          { title: 'Heading 5', format: 'h5' },
          { title: 'Heading 6', format: 'h6' }
        ]},
        { title: 'Inline', items: [
          { title: 'Bold', format: 'bold' },
          { title: 'Italic', format: 'italic' },
          { title: 'Underline', format: 'underline' },
          { title: 'Strikethrough', format: 'strikethrough' },
          { title: 'Superscript', format: 'superscript' },
          { title: 'Subscript', format: 'subscript' },
          { title: 'Code', format: 'code' },
          { title: 'Highlight', format: 'highlight' },
          { title: 'Blockquote', format: 'blockquote' }
        ]},
        { title: 'Align', items: [
          { title: 'Left', format: 'alignleft' },
          { title: 'Center', format: 'aligncenter' },
          { title: 'Right', format: 'alignright' },
          { title: 'Justify', format: 'alignjustify' }
        ]}
      ],
      font_formats: "Andale Mono=andale mono,times; Arial=arial,helvetica,sans-serif; Arial Black=arial black,avant garde; Book Antiqua=book antiqua,palatino; Calibri=calibri; Courier New=courier new,courier; Georgia=georgia,palatino; Helvetica=helvetica; Impact=impact,chicago; Symbol=symbol; Tahoma=tahoma,arial,helvetica,sans-serif; Terminal=terminal,monaco; Times New Roman=times new roman,times; Trebuchet MS=trebuchet ms,geneva; Verdana=verdana,geneva;",
});

lessVariables:
@background-color: #fff;
@toolbar-background-color: rgba(255, 255, 255, 1);
@menubar-background-color: rgba(255, 255, 255, 1);
@border-color: #ccc;
@color-black: #222f3e;
@color-white: #fff;
@color-tint: rgba(109, 112, 167, 1);
@color-error: rgba(255, 126, 121, 1);
@color-success: rgba(80, 176, 113, 1);
@base-value: 16px;
@control-border-radius: 3px;
@panel-border-radius: 3px;
@tinymce-border-radius: 0px;
@font-stack: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-text-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-hover-background-color: multiply(white, contrast(@background-color, fade(@color-black, 15), fade(@color-black, 85)));
@toolbar-button-hover-icon-color: @toolbar-button-icon-color;
@toolbar-button-hover-text-color: @toolbar-button-icon-color;
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
